Flip card on hover.body:
.flip-card {
  background-color: transparent;
  width: 200px;
  height: 200px;
  perspective: 1000px;
}
.flip-card-inner {
  position: relative;
  width: 100%;
  height: 100%;
  text-align: center;
  transition: transform 0.6s;
  transform-style: preserve-3d;
  box-shadow: 0 4px 8px 0 rgba(0,0,0,0.2);
}
.flip-card:hover .flip-card-inner {
  transform: rotateY(180deg);
}
.flip-card-front, .flip-card-back {
  position: absolute;
  width: 100%;
  height: 100%;
  backface-visibility: hidden;
}
.flip-card-front {
  background-color: #bbb;
  color: black;
  z-index: 2;
}
.flip-card-back {
  background-color: #2980b9;
  color: white;
  transform: rotateY(180deg);
  z-index: 1;
}

sticky sidebar collapse to icons with re.body:
#wrapper {
  padding-left: 0;
  -webkit-transition: all 0.6s ease;
  -moz-transition: all 0.6s ease;
  -o-transition: all 0.6s ease;
  transition: all 0.6s ease;
}
#wrapper.toggled {
  padding-left: 200px;
}
#sidebar-wrapper {
  z-index: 1000;
  position: fixed;
  left: 250px;
  width: 0;
  height: 100%;
  margin-left: -250px;
  overflow-y: auto;
  background-color:#312A25 !Important;
  -webkit-transition: all 0.5s ease;
  -moz-transition: all 0.5s ease;
  -o-transition: all 0.5s ease;
  transition: all 0.5s ease;
}
#wrapper.toggled #sidebar-wrapper {
   width: 0;
}
#page-content-wrapper {
   width: 100%;
   position: absolute;
   padding: 10px;
}
#wrapper.toggled #page-content-wrapper {
   position: absolute;
   margin-left:-250px;
}
.sidebar-nav {
   position: absolute;
   top: 0;
   right:15px;
   width: 200px;
   margin: 0;
   padding: 0;
   list-style: none;
}
.sidebar-nav li {
   text-indent: 20px;
   line-height: 40px;
}
.sidebar-nav li a {
   display: block;
   text-decoration: none;
   color: #999999;
}
.sidebar-nav li a:hover {
   text-decoration: none;
   color: #fff;
   background: #312A25;
}
.sidebar-nav li a:active,
.sidebar-nav li a:focus {
   text-decoration: none;
}
.sidebar-nav > .sidebar-brand {
   height: 60px;
   font-size: 18px;
}
.sidebar-nav > .sidebar-brand a {
   color: #999999;
}
.sidebar-nav > .sidebar-brand a:hover {
   color: #fff;
   background: none;
}
@media(min-width:768px) {
  #wrapper {
    padding-left: 220px;
  }
  #wrapper.toggled {
    padding-left: 0;
  }
  #sidebar-wrapper {
    width: 200px;
  }
  #wrapper.toggled #sidebar-wrapper {
    width: 40px;
  }
  #wrapper.toggled span {
    visibility:hidden;
  }
  #wrapper.toggled   i {
    float:right;
  }
  #page-content-wrapper {
    padding: 20px;
    position: relative;
  }
   #wrapper.toggled #page-content-wrapper {
     position: relative;
     margin-right: 0;
   }
}
@media(max-width:414px) {
  #wrapper.toggled #page-content-wrapper {
    position: absolute;
    margin-right:60px;
  }
  #wrapper.toggled {
    padding-right: 60px;
  }
  #wrapper {
    padding-left: 20px;
  }
  #wrapper.toggled {
    padding-left: 0;
  }
  #sidebar-wrapper {
    width: 50px;
  }
  #wrapper.toggled #sidebar-wrapper {
     width: 140px;
  }
  #wrapper.toggled span {
     visibility:visible;
     position:relative;
     left:70px;
     bottom:13px;
  }
  #wrapper span {
     visibility:hidden;
   }
  #wrapper.toggled   i {
    float:right;
  }
  #wrapper i {
    float:right;
  }
  #page-content-wrapper {
    padding: 5px;
    position: relative;
  }
  #wrapper.toggled #page-content-wrapper {
    position: relative;
    margin-right: 0;
  }
}
